#define RunUo2_0

using System;
using Server;
using Server.Gumps;
using Server.Network;
using Server.Commands;
using Server.Mobiles;
using Server.Fronteira.Talentos;
using Server.Items;
using System.Collections.Generic;

namespace Server.Gumps
{
    public class GumpHabilidades : Gump
    {
        private List<Talento> Talentos;

        public static void Initialize()
        {
            CommandSystem.Register("habilidades", AccessLevel.Player, new CommandEventHandler(_OnCommand));
            CommandSystem.Register("usarhabilidade", AccessLevel.Player, new CommandEventHandler(_OnCommand2));
        }

        [Usage(".usarhabilidade <numero>")]
        public static void _OnCommand2(CommandEventArgs e)
        {
            var caller = e.Mobile as PlayerMobile;
            if (e.Length <= 0)
            {
                caller.SendMessage("Use .usarhabilidade <numero>");
                return;
            }
            try
            {
                var index = e.GetInt32(0);
                var talentos = caller.Talentos.Habilidades();
                if(index >= talentos.Count)
                {
                    var habilidade = WeaponAbility.Talentos[talentos[index]];
                    WeaponAbility.SetCurrentAbility(caller, habilidade);
                    if(caller.HasGump(typeof(GumpHabilidades)))
                    {
                        caller.CloseGump(typeof(GumpHabilidades));
                        caller.SendGump(new GumpHabilidades(caller, talentos));
                    }
                    caller.SendMessage("Voce tem "+talentos.Count+" habilidades entao o numero precisa ser menor que isto");
                    return;
                }

            }
            catch (Exception ex)
            {
                caller.SendMessage("Use .usarhabilidade <numero>");
                return;
            }
        }

        [Usage(".habilidades")]
        public static void _OnCommand(CommandEventArgs e)
        {
            var caller = e.Mobile as PlayerMobile;
            if (caller == null)
                return;

            var habs = caller.Talentos.Habilidades();

            if (habs.Count == 0)
            {
                caller.SendMessage("Aprenda habilidades aprendendo talentos usando .xp ou por pergaminhos pelo mundo");
                return;
            }

            if (caller.HasGump(typeof(GumpHabilidades)))
                caller.CloseGump(typeof(GumpHabilidades));
            caller.SendGump(new GumpHabilidades(caller, habs));
        }

        public GumpHabilidades(PlayerMobile player) : base(0, 0)
        {
            var habs = player.Talentos.Habilidades();
            Mostra(player, habs);
        }

        public GumpHabilidades(PlayerMobile player, List<Talento> habilidades) : base(0, 0)
        {
            Mostra(player, habilidades);
        }

        public void Mostra(PlayerMobile player, List<Talento> habilidades)
        {
            Talentos = habilidades;
            this.Closable = true;
            this.Disposable = true;
            this.Dragable = true;
            this.Resizable = false;

            AddPage(0);
            AddBackground(191, 177, 231, 70, 9350);

            var botao = 1;
            var x = 0;
            foreach (var talentoHabilidade in habilidades)
            {
                var habilidade = WeaponAbility.Talentos[talentoHabilidade];
                var def = DefTalentos.GetDef(talentoHabilidade);
                AddButton(202 + x, 196, def.Icone, def.Icone, botao, GumpButtonType.Reply, 0);
                if (WeaponAbility.GetCurrentAbility(player) == habilidade)
                {
                    AddImage(214 + x, 182, 9906);
                }
                botao++;
                x += 52;
            }

            if(!player.IsCooldown("dicahabs"))
            {
                player.SetCooldown("dicahabs", TimeSpan.FromHours(3));
                player.SendMessage(78, "Voce pode usar o comando .usarhabilidade para usar habilidades por macro");
            }
        }



        public override void OnResponse(NetState sender, RelayInfo info)
        {
            Mobile from = sender.Mobile;
            Shard.Debug("Button " + info.ButtonID);
            if (info.ButtonID != 0)
            {
                var ativado = Talentos[info.ButtonID - 1];
                var habilidade = WeaponAbility.Talentos[ativado];
                WeaponAbility.SetCurrentAbility(from, habilidade);
                from.CloseGump(typeof(GumpHabilidades));
                from.SendGump(new GumpHabilidades(from as PlayerMobile, Talentos));
            }
        }
    }
}
